import React, { Component } from 'react';
//import Button from 'antd/es/button';
import axios from 'axios';
import {
  Form,
  Input,
  Tooltip,
  Icon,
  Cascader,
  Select,
  Row,
  Col,
  Checkbox,
  Button,
  AutoComplete,
  DatePicker ,
} from 'antd';
import moment from 'moment'
import './App.css';

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;
const formTailLayout = {
  labelCol: { span: 4 },
  wrapperCol: { span: 8, offset: 4 },
};

const departments = [
  {
    value: 'Department1',
    label: 'Department1',
    children: [
      {
        value: 'Title1',
        label: 'Title1',

      },
    ],
  },
  {
    value: 'Department2',
    label: 'Department2',
    children: [
      {
        value: 'Title2',
        label: 'Title2',

      },
    ],
  },
];


class RegistrationForm extends React.Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
  };

  onSubmit = () => {

    axios.post(`http://localhost:8080/add`,{

      fullName: "Yasmin Marwan",
      birthDate: "2019-02-02",
      email: "y@mail.com",
      depName: "Human Resources",
      titleName: "HR Manager",
      isLeader:false

    }  )
        .then(res => {
          console.log(res);
          console.log(res.data);
        })
  };



  check = () => {
    this.props.form.validateFields(err => {
      if (!err) {
        console.info('success');
      }
    });
  };

  handleChange = e => {
    this.setState(
        {
          checkNick: e.target.checked,
        },
    );
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        //getFieldValue('fullName');
        values.title = values.title[1];
        values.date =  moment(values.date).format("YYYY-MM-DD");

        //const date = moment(values.date).format("YYYY-MM-DD");

        console.log('Received values of form: ', values);

      }
    });
  };




  render() {
    const config = {
      rules: [{ type: 'object', required: true, message: 'Please select time!' }],
    };
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };
    // const prefixSelector = getFieldDecorator('prefix', {
    //   initialValue: '86',
    // })(
    //   <Select style={{ width: 70 }}>
    //     <Option value="86">+86</Option>
    //     <Option value="87">+87</Option>
    //   </Select>,
    // );

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
      <Form {...formItemLayout} onSubmit={this.handleSubmit}>
        <Form.Item {...formItemLayout} label="FullName">
          {getFieldDecorator('fullName', {
            rules: [
              {
                required: true,
                message: 'Please input your name',
              },
            ],
          })(<Input placeholder="Please input your Full Name" />)}
        </Form.Item>

        <Form.Item label="E-mail">
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: 'The input is not valid E-mail!',
              }, 
              {
                required: true,
                message: 'Please input your E-mail!',
              },
            ],
          })(<Input />)}
        </Form.Item>

        <Form.Item label="BirthDate">
          {getFieldDecorator('birthDate', config)(
            <DatePicker showTime format="YYYY-MM-DD" />,
          )}
        </Form.Item>

        <Form.Item label="Title">
          {getFieldDecorator('title', {

            rules: [
              { type: 'array', required: true, message: 'Please select your Title' },
            ],
          })(<Cascader options={departments} />)}
        </Form.Item>

        <Form.Item {...tailFormItemLayout}>

          <Checkbox checked={this.state.checkNick} onChange={this.handleChange}>
            Leader
          </Checkbox>

        </Form.Item>



        <Form.Item {...tailFormItemLayout}>
          <Button type="primary"  htmlType="submit" >
            Register
          </Button>
        </Form.Item>
      </Form>
    );
  }
}



export default RegistrationForm;
